mapper.select:
select cs.schedule_no AS scheduleNo ,cs.schedule_day AS scheduleDay ,cs.schedule_period AS schedulePeriod ,cs.schedule_registered_date AS scheduleRegisteredDate ,cs.schedule_modification_date AS scheduleModificationDate ,co.course_name AS courseName ,sub.subject_name AS subjectName ,cra.course_assignment_no AS courseAssignmentNo ,aro.room_number AS roomNumber ,aro.room_floor AS roomFloor ,aro.room_usage AS roomUsage ,tea.member_id AS memberId ,mem.member_name AS memberName from course_schedule AS cs ,course_room_assignment AS cra ,course AS co ,subject AS sub ,academy_room AS aro ,teacher AS tea ,member_login AS login ,member AS mem where cs.course_assignment_no = cra.course_assignment_no and cra.course_no = co.course_no and co.subject_no = sub.subject_no and cra.room_no = aro.room_no and tea.course_no = co.course_no and tea.member_id = login.member_id and login.member_id = mem.member_id and login.member_level = '강사'
and cs.schedule_no = #{scheduleNo}
and cs.schedule_approval_status = #{scheduleApprovalStatus}
and co.subject_no = #{subjectNo}
and cra.course_no = #{courseNo}
and cra.room_no = #{roomNo}
and tea.member_id = #{memberId}
and cs.schedule_day = #{scheduleDay}
order by cs.schedule_day asc ,cs.schedule_period asc

mapper.update:
update
	course_schedule

		 course_assignment_no			= #{courseAssignmentNo}
	
		,schedule_day					= #{scheduleDay}
	
	
		,schedule_period				= #{schedulePeriod}
	
		,schedule_approval_status		= #{scheduleApprovalStatus}
		,schedule_modification_date		= curdate()

where
	schedule_no = #{scheduleNo}
